.ui-radio {
    position: relative;
    margin: 0 20px 10px;
    font-size: 14px;
    line-height: 20px;
    height: 20px
}

.ui-radio input[type=radio]+span:hover {
    cursor: pointer
}

.ui-radio input[type=radio]+span:hover:before {
    border-color: #5b90bf
}

.ui-radio input[type=radio]:disabled+span:hover {
    cursor: not-allowed
}

.ui-radio input[type=radio]:disabled+span:hover:before {
    border-color: #ccc
}

.ui-radio input[type=radio] {
    display: none
}

.ui-radio input[type=radio]+span {
    padding-left: 10px;
    font-weight: 400
}

.ui-radio input[type=radio]+span:before {
    content: "";
    width: 20px;
    height: 20px;
    display: inline-block;
    vertical-align: middle;
    position: absolute;
    left: -20px;
    top: 0;
    background: #fff;
    border-radius: 50%;
    border: 1px solid #ccc;
    -webkit-transition: all .15s ease-in-out;
    -moz-transition: all .15s ease-in-out;
    transition: all .15s ease-in-out
}

.ui-radio input[type=radio]:disabled+span:before {
    opacity: .65;
    border: 1px solid #ccc;
    cursor: no-drop
}

.ui-radio input[type=radio]:checked+span:before {
    border-color: #5b90bf
}

.ui-radio input[type=radio]:checked+span:after {
    content: "";
    width: 8px;
    height: 8px;
    position: absolute;
    top: 6px;
    left: -14px;
    background-color: #5b90bf;
    border-radius: 50%;
